mapper.select:
select

from tb_merchant_account ma

  ma.is_delete =0

    AND ma.phone = #{phone,jdbcType=VARCHAR}


    AND ma.name = #{name,jdbcType=VARCHAR}


    AND ma.account_type = #{accountType,jdbcType=INTEGER}


    AND  EXISTS (
      select mas.merchant_account_id
      from tb_merchant_account_bind_shop mas
      where mas.shop_id in

        #{item}

      )


order by ma.create_time DESC
limit #{startNum}, #{pageSize}

select count(1)
from tb_merchant_account ma

  ma.is_delete =0

    AND ma.phone = #{phone,jdbcType=VARCHAR}


    AND ma.name = #{name,jdbcType=VARCHAR}


    AND ma.account_type = #{accountType,jdbcType=INTEGER}


    AND  EXISTS (
    select mas.merchant_account_id
    from tb_merchant_account_bind_shop mas
    where mas.shop_id in

      #{item}

    )
