mapper.select:
select

from tb_merchant_activity_code mac,
     tb_merchant m,
     tb_terminal_device d


  mac.mch_code=m.mch_code and mac.device_id=d.id and mac.is_delete=0

    AND m.name = #{shopName,jdbcType=VARCHAR}


    AND d.name = #{deviceName,jdbcType=VARCHAR}


order by ctime DESC
limit #{startNum}, #{pageSize}

select count(1)
from tb_merchant_activity_code mac,
tb_merchant m,
tb_terminal_device d


  mac.mch_code=m.mch_code and mac.device_id=d.id and mac.is_delete=0

    AND m.name = #{shopName,jdbcType=VARCHAR}


    AND d.name = #{deviceName,jdbcType=VARCHAR}
