mapper.select:
select

from tb_role_info
where is_delete=0
and foreign_id=#{foreignId}
and system_id=#{systemId}
order by display_order DESC

select

from tb_role_info


  is_delete =0

    AND role_name = #{roleName,jdbcType=VARCHAR}


    AND system_id = #{systemId,jdbcType=INTEGER}


    AND foreign_id = #{foreignId,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}


    AND role_type = #{roleType,jdbcType=INTEGER}


order by create_time DESC
limit #{startNum}, #{pageSize}

select count(1) from tb_role_info

  is_delete =0

    AND role_name = #{roleName,jdbcType=VARCHAR}


    AND system_id = #{systemId,jdbcType=INTEGER}


    AND foreign_id = #{foreignId,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}


    AND role_type = #{roleType,jdbcType=INTEGER}

select

from tb_role_info
where is_delete=0
and foreign_id=#{foreignId}
and role_type = #{roleType}
order by display_order DESC
