mapper.select:
select

from tb_withdraw_record wr ,tb_merchant_account ma


  wr.operator_id=ma.id

    AND wr.business_no = #{businessNo,jdbcType=VARCHAR}


    AND wr.withdraw_account = #{withdrawAccount,jdbcType=INTEGER}


    AND wr.withdraw_type = #{withdrawType,jdbcType=INTEGER}


    AND wr.status = #{status,jdbcType=INTEGER}



    and wr.create_time>=#{startTime}




    and wr.create_time<=#{endTime}



order by create_time DESC
limit #{startNum}, #{pageSize}

select count(1) from tb_withdraw_record


    AND business_no = #{businessNo,jdbcType=VARCHAR}


    AND withdraw_account = #{withdrawAccount,jdbcType=INTEGER}


    AND withdraw_type = #{withdrawType,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}



    and create_time>=#{startTime}




    and create_time<=#{endTime}

select sum(amount) from tb_withdraw_record


    AND business_no = #{businessNo,jdbcType=VARCHAR}


    AND withdraw_account = #{withdrawAccount,jdbcType=INTEGER}


    AND withdraw_type = #{withdrawType,jdbcType=INTEGER}


    AND status = #{status,jdbcType=INTEGER}



    and create_time>=#{startTime}




    and create_time<=#{endTime}
